INSERT INTO `email_templates` 
(`email_templates_code`, `email_templates_subject`, `email_templates_message`, `email_templates_variable`)
 VALUES
('payment_notify_renewal_success', 'Your Payment with Japan Macro Advisors', '<html xmlns="http://www.w3.org/1999/xhtml">\n<head>\n<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />\n<title>Welcome to Japan Macro Advisors</title>\n</head>\n<body>\n	<div style="width:818px;float:left; font-family:Arial;border:1px solid black">\n    <div style="float:left; padding-left:80px;padding-top:10px;">\n    <img src="http://japanmacroadvisors.com/images/mail_template/logo.png" />\n    \n			<h3>Welcome to Japan Macro Advisors</h3>\n			</div>\n<div>\n<img src="http://japanmacroadvisors.com/images/mail_template/jmabanner.jpg" />\n</div><br />		\n<div style="width:655px; float:left; margin-top:-10px; padding-left:80px">\n		\n			<p style="font-size:14px;">Dear <b>{{name}}</b>,</p>\n<p style="font-size:14px;">We hope you have been enjoying reading updates on Japanâ€™s economy. Your monthly Premium \nsubscription with the email address {{email}} has reached its renewal date. </p>\n<p style="font-size:14px;">Your credit card was charged {{currency}} {{amount}} on {{date}} and your service remains uninterrupted.</p>\n<p style="font-size:14px;">For any assistance, contact our <a style="font-size:14px;" href="http://japanmacroadvisors.com/helpdesk/post/">Helpdesk</a> or <a style="font-size:14px;" href="http://japanmacroadvisors.com/contact">Support</a></p>\n<p style="font-size:14px;">You can access your account <a style="font-size:14px;" href="http://japanmacroadvisors.com/user/myaccount">My account</a></p>\n<p style="font-size:14px;">Thank you,\n<br>Japan Macro Advisors\n<br><a style="font-size:14px;" href="http://japanmacroadvisors.com/">www.japanmacroadvisors.com</a></p>\n    		\n       \n	</div>\n</div>\n</body>\n</html>', '{name},{email},{currency},{amount}');
